import React, { useState, useEffect } from 'react';
import { Timer, Zap, Crown } from 'lucide-react';
import io from 'socket.io-client';

const getSocketURL = () => {
  const protocol = window.location.protocol;
  const hostname = window.location.hostname;
  const port = process.env.NODE_ENV === 'production' ? window.location.port : '3001';
  return `${protocol}//${hostname}:${port}`;
};

export default function CentralDisplay() {
  const [socket, setSocket] = useState(null);
  const [gameState, setGameState] = useState(null);
  const [prompts, setPrompts] = useState({});
  const [timer, setTimer] = useState(0);
  const [images, setImages] = useState({});

  useEffect(() => {
    const socketURL = getSocketURL();
    console.log('Central Display connecting to:', socketURL);

    const newSocket = io(socketURL, {
      transports: ['websocket', 'polling']
    });

    newSocket.on('connect', () => {
      console.log('Central Display connected');
      newSocket.emit('join-display');
    });

    newSocket.on('game-state', (state) => {
      console.log('Game state updated:', state);
      setGameState(state);
      if (state.generatedImages) {
        setImages(state.generatedImages);
      }
    });

    newSocket.on('prompt-update', ({ playerId, prompt }) => {
      setPrompts(prev => ({ ...prev, [playerId]: prompt }));
    });

    newSocket.on('battle-started', ({ duration }) => {
      setTimer(duration);
      setPrompts({});
      setImages({});
    });

    newSocket.on('timer-update', (timeLeft) => {
      setTimer(timeLeft);
    });

    newSocket.on('images-ready', (generatedImages) => {
      setImages(generatedImages);
    });

    newSocket.on('winner-selected', (winnerId) => {
      console.log('Winner selected:', winnerId);
    });

    setSocket(newSocket);

    return () => {
      newSocket.close();
    };
  }, []);

  const selectWinner = (playerId) => {
    if (socket && gameState?.phase === 'judging') {
      socket.emit('select-winner', playerId);
    }
  };

  const formatTime = (seconds) => {
    const mins = Math.floor(seconds / 60);
    const secs = seconds % 60;
    return `${mins}:${secs.toString().padStart(2, '0')}`;
  };

  const getTimerColor = () => {
    if (timer <= 10) return 'text-red-500 animate-pulse';
    if (timer <= 30) return 'text-orange-500';
    return 'text-green-500';
  };

  const getPhaseTitle = () => {
    switch (gameState?.phase) {
      case 'waiting':
        return 'Waiting for Players';
      case 'ready':
        return 'Get Ready!';
      case 'battling':
        return 'PROMPT BATTLE IN PROGRESS!';
      case 'generating':
        return 'Generating Images...';
      case 'judging':
        return 'Choose the Winner!';
      case 'finished':
        return `Player ${gameState.winner} Wins! 🎉`;
      default:
        return 'Prompt Battle';
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-indigo-900 via-purple-900 to-pink-900 text-white">
      {/* Header */}
      <div className="p-8 text-center border-b border-purple-600">
        <h1 className="text-6xl font-bold mb-4 bg-gradient-to-r from-yellow-400 to-pink-400 bg-clip-text text-transparent">
          🎨 PROMPT BATTLE 🎨
        </h1>
        <div className="text-2xl font-semibold">
          {getPhaseTitle()}
        </div>

        {gameState?.phase === 'battling' && (
          <div className={`text-8xl font-mono font-bold mt-4 ${getTimerColor()}`}>
            <Timer className="inline h-16 w-16 mr-4" />
            {formatTime(timer)}
          </div>
        )}
      </div>

      {/* Target Display */}
      {gameState?.target && gameState?.phase !== 'waiting' && (
        <div className="p-8 text-center">
          <div className="bg-white/10 backdrop-blur rounded-xl p-8 max-w-4xl mx-auto">
            <h2 className="text-3xl font-bold mb-4 text-yellow-400">TARGET:</h2>
            <p className="text-2xl font-semibold">{gameState.target}</p>
          </div>
        </div>
      )}

      {/* Live Prompts Display */}
      {gameState?.phase === 'battling' && (
        <div className="p-8">
          <div className="grid md:grid-cols-2 gap-8 max-w-6xl mx-auto">
            {['1', '2'].map(playerId => (
              <div key={playerId} className="bg-white/5 backdrop-blur rounded-xl p-6 border-2 border-purple-400">
                <h3 className="text-2xl font-bold mb-4 text-center text-blue-300">
                  Player {playerId}
                </h3>
                <div className="min-h-48 p-4 bg-black/20 rounded-lg">
                  <p className="text-lg font-mono whitespace-pre-wrap">
                    {prompts[playerId] || 'Thinking...'}
                    <span className="animate-pulse">|</span>
                  </p>
                </div>
              </div>
            ))}
          </div>
        </div>
      )}

      {/* Image Generation Loading */}
      {gameState?.phase === 'generating' && (
        <div className="p-8 text-center">
          <div className="inline-block animate-spin rounded-full h-24 w-24 border-b-4 border-white mb-8"></div>
          <p className="text-3xl font-semibold">Creating magical images...</p>
          <p className="text-xl text-gray-300 mt-4">This may take a moment</p>
        </div>
      )}

      {/* Image Display and Judging */}
      {gameState?.phase === 'judging' && (
        <div className="p-8">
          <div className="grid md:grid-cols-2 gap-8 max-w-6xl mx-auto">
            {Object.entries(images).map(([playerId, imageData]) => (
              <div key={playerId} className="bg-white/10 backdrop-blur rounded-xl p-6">
                <h3 className="text-2xl font-bold mb-4 text-center text-blue-300">
                  Player {playerId}
                </h3>

                {imageData.url && (
                  <div className="mb-4">
                    <img 
                      src={imageData.url} 
                      alt={`Player ${playerId}'s creation`}
                      className="w-full h-64 object-cover rounded-lg border-4 border-purple-400"
                      onError={(e) => {
                        e.target.src = `https://via.placeholder.com/512x512/4a5568/ffffff.png?text=Image+Error`;
                      }}
                    />
                    {imageData.fallback && (
                      <p className="text-yellow-400 text-sm mt-2">⚠️ Fallback image (SD unavailable)</p>
                    )}
                  </div>
                )}

                <div className="bg-black/30 p-4 rounded-lg mb-4">
                  <h4 className="text-sm font-semibold text-gray-300 mb-2">Prompt:</h4>
                  <p className="text-sm font-mono">{imageData.prompt}</p>
                </div>

                <button
                  onClick={() => selectWinner(playerId)}
                  className="w-full bg-gradient-to-r from-yellow-500 to-orange-500 hover:from-yellow-600 hover:to-orange-600 text-white font-bold py-4 px-6 rounded-lg text-xl transition-all transform hover:scale-105 flex items-center justify-center space-x-2"
                >
                  <Crown className="h-6 w-6" />
                  <span>Choose Winner!</span>
                </button>
              </div>
            ))}
          </div>
        </div>
      )}

      {/* Winner Celebration */}
      {gameState?.phase === 'finished' && gameState.winner && (
        <div className="p-8 text-center">
          <div className="animate-bounce mb-8">
            <div className="text-9xl mb-4">🏆</div>
            <h2 className="text-6xl font-bold text-yellow-400 mb-4">
              PLAYER {gameState.winner} WINS!
            </h2>
          </div>

          {images[gameState.winner] && (
            <div className="max-w-2xl mx-auto">
              <h3 className="text-2xl font-semibold mb-4">Winning Creation:</h3>
              <img 
                src={images[gameState.winner].url}
                alt="Winning creation"
                className="w-full h-96 object-cover rounded-xl border-4 border-yellow-400 shadow-2xl"
              />
              <div className="bg-white/10 backdrop-blur rounded-lg p-4 mt-4">
                <p className="text-lg font-mono">{images[gameState.winner].prompt}</p>
              </div>
            </div>
          )}
        </div>
      )}

      {/* Connection Info */}
      {gameState?.phase === 'waiting' && (
        <div className="p-8 text-center">
          <div className="text-6xl mb-8">⏳</div>
          <p className="text-2xl mb-8">Waiting for players to connect...</p>

          <div className="grid md:grid-cols-2 gap-8 max-w-4xl mx-auto">
            <div className="bg-white/10 backdrop-blur rounded-xl p-6">
              <h3 className="text-xl font-bold mb-4 text-green-400">Player 1</h3>
              <p className={gameState?.players?.['1']?.connected ? 'text-green-400' : 'text-gray-400'}>
                {gameState?.players?.['1']?.connected ? '✓ Connected' : '○ Waiting...'}
              </p>
            </div>

            <div className="bg-white/10 backdrop-blur rounded-xl p-6">
              <h3 className="text-xl font-bold mb-4 text-blue-400">Player 2</h3>
              <p className={gameState?.players?.['2']?.connected ? 'text-green-400' : 'text-gray-400'}>
                {gameState?.players?.['2']?.connected ? '✓ Connected' : '○ Waiting...'}
              </p>
            </div>
          </div>

          <div className="mt-8 text-gray-300">
            <p>Players should visit:</p>
            <p className="font-mono text-lg mt-2">
              {window.location.origin}/player/1 and {window.location.origin}/player/2
            </p>
          </div>
        </div>
      )}
    </div>
  );
}
