import React, { useState, useEffect } from 'react';
import io from 'socket.io-client';

const getSocketURL = () => {
  const protocol = window.location.protocol;
  const hostname = window.location.hostname;
  const port = process.env.NODE_ENV === 'production' ? window.location.port : '3001';
  return `${protocol}//${hostname}:${port}`;
};

export default function CentralDisplay() {
  const [gameState, setGameState] = useState(null);

  // Simple socket connection
  useEffect(() => {
    const socketURL = getSocketURL();
    const socket = io(socketURL);

    socket.on('connect', () => {
      console.log('Connected to server');
      socket.emit('join-display');
    });

    socket.on('game-state', (state) => {
      console.log('Game state:', state);
      setGameState(state);
    });

    return () => socket.close();
  }, []);

  return (
    <div className="min-h-screen bg-white text-black" style={{
      fontFamily: 'Chicago, "SF Pro Display", system-ui, sans-serif',
      fontSize: '9px',
      backgroundImage: `url("data:image/svg+xml,%3Csvg width='4' height='4' xmlns='http://www.w3.org/2000/svg'%3E%3Cpath d='M0,0 L2,2 M2,0 L4,2 M0,2 L2,4 M2,2 L4,4' stroke='%23ddd' stroke-width='0.5' fill='none'/%3E%3C/svg%3E")`,
      backgroundSize: '4px 4px'
    }}>
      {/* Menu Bar */}
      <div className="bg-white border-b border-black h-5 flex items-center px-2 text-xs font-bold">
        <span className="mr-4" style={{
          fontFamily: 'Brush Script MT, cursive',
          fontSize: '14px',
          fontWeight: 'bold'
        }}>Kym</span>
        <span className="mr-4">File</span>
        <span className="mr-4">Edit</span>
        <span className="mr-4">View</span>
        <span className="mr-4">Special</span>
        <div className="ml-auto flex items-center">
          <span className="mr-2 text-xs">Central Display</span>
          <div className="w-1.5 h-1.5 bg-black"></div>
        </div>
      </div>

      {/* Desktop */}
      <div className="px-8 pt-2 pb-16" style={{ minHeight: 'calc(100vh - 20px)', position: 'relative' }}>
        {/* Main Window */}
        <div className="bg-white border-2 border-black" style={{
          boxShadow: '4px 4px 0px black',
          minHeight: 'calc(100vh - 140px)',
          marginLeft: '40px',
          marginRight: '40px'
        }}>
          {/* Window Title Bar */}
          <div className="h-4 bg-white border-b border-black flex items-center justify-between px-2 relative">
            <div className="absolute inset-0 flex flex-col justify-center">
              {[...Array(6)].map((_, i) => (
                <div key={i} className="h-px bg-black opacity-30" style={{marginTop: '1px'}}></div>
              ))}
            </div>
            <div className="flex items-center relative z-10 bg-white px-1">
              <div className="w-2 h-2 border border-black bg-white mr-1"></div>
              <span className="text-xs font-bold">Prompt Battle - Central Display</span>
            </div>
          </div>

          {/* Main Content */}
          <div className="p-4">
            <div className="text-center">
              <h1 className="text-2xl font-bold mb-4" style={{ fontSize: '24px' }}>
                Prompt Battle
              </h1>
              <p style={{ fontSize: '16px' }}>
                Game State: {gameState?.phase || 'waiting'}
              </p>
              <p style={{ fontSize: '14px' }} className="mt-2">
                Connected Players: {Object.keys(gameState?.players || {}).length}
              </p>

              {/* Simple waiting display */}
              {(!gameState || gameState.phase === 'waiting') && (
                <div className="mt-8">
                  <div className="text-6xl mb-8">‚è≥</div>
                  <p style={{ fontSize: '18px' }} className="mb-4">Players can visit:</p>
                  <div className="space-y-2">
                    <div className="border border-black p-4 bg-gray-100">
                      <strong>Player 1:</strong> {window.location.origin}/player/1
                    </div>
                    <div className="border border-black p-4 bg-gray-100">
                      <strong>Player 2:</strong> {window.location.origin}/player/2
                    </div>
                  </div>
                </div>
              )}
            </div>
          </div>
        </div>

        {/* Simple Desktop Icons */}
        <div className="absolute bottom-4 right-8 flex gap-4">
          <div className="flex flex-col items-center">
            <div className="w-8 h-8 bg-white border-2 border-black flex items-center justify-center">
              <span style={{ fontSize: '10px' }}>üí¨</span>
            </div>
            <div className="bg-white px-1 text-black text-xs font-bold">KymChat</div>
          </div>
          <div className="flex flex-col items-center">
            <div className="w-8 h-8 bg-white border-2 border-black flex items-center justify-center">
              <span style={{ fontSize: '10px' }}>üõ°Ô∏è</span>
            </div>
            <div className="bg-white px-1 text-black text-xs font-bold">Sentinel</div>
          </div>
          <div className="flex flex-col items-center">
            <div className="w-8 h-8 bg-white border-2 border-black flex items-center justify-center">
              <span style={{ fontSize: '10px' }}>üìã</span>
            </div>
            <div className="bg-white px-1 text-black text-xs font-bold">CRM</div>
          </div>
          <div className="flex flex-col items-center">
            <div className="w-8 h-8 bg-white border-2 border-black flex items-center justify-center">
              <span style={{ fontSize: '10px' }}>üîÑ</span>
            </div>
            <div className="bg-white px-1 text-black text-xs font-bold">Retain</div>
          </div>
        </div>
      </div>
    </div>
  );
}
