import React, { useState, useEffect, useCallback } from 'react';
import { Timer, Wifi, WifiOff, Zap, AlertCircle, Send } from 'lucide-react';
import io from 'socket.io-client';

// Dynamic socket URL that works for both local and network access
const getSocketURL = () => {
  const protocol = window.location.protocol;
  const hostname = window.location.hostname;
  const port = process.env.NODE_ENV === 'production' ? window.location.port : '3001';
  return `${protocol}//${hostname}:${port}`;
};

export default function PlayerInterface({ playerId }) {
  const [socket, setSocket] = useState(null);
  const [prompt, setPrompt] = useState('');
  const [gameState, setGameState] = useState(null);
  const [connected, setConnected] = useState(false);
  const [error, setError] = useState(null);
  const [timer, setTimer] = useState(0);

  useEffect(() => {
    const socketURL = getSocketURL();
    console.log(`Player ${playerId} connecting to:`, socketURL);

    const newSocket = io(socketURL, {
      transports: ['websocket', 'polling']
    });

    newSocket.on('connect', () => {
      console.log(`Player ${playerId} connected`);
      setConnected(true);
      setError(null);
      newSocket.emit('join-player', playerId);
    });

    newSocket.on('disconnect', () => {
      console.log(`Player ${playerId} disconnected`);
      setConnected(false);
    });

    newSocket.on('game-state', (state) => {
      console.log('Game state updated:', state);
      setGameState(state);
    });

    newSocket.on('battle-started', ({ duration }) => {
      setTimer(duration);
    });

    newSocket.on('timer-update', (timeLeft) => {
      setTimer(timeLeft);
    });

    newSocket.on('connect_error', (err) => {
      console.error('Connection error:', err);
      setError('Failed to connect to server');
      setConnected(false);
    });

    setSocket(newSocket);

    return () => {
      newSocket.close();
    };
  }, [playerId]);

  const handlePromptChange = useCallback((e) => {
    const value = e.target.value;
    setPrompt(value);

    if (socket && gameState?.phase === 'battling') {
      socket.emit('prompt-update', { playerId, prompt: value });
    }
  }, [socket, gameState?.phase, playerId]);

  const handleReady = () => {
    if (socket) {
      socket.emit('player-ready', playerId);
    }
  };

  const formatTime = (seconds) => {
    const mins = Math.floor(seconds / 60);
    const secs = seconds % 60;
    return `${mins}:${secs.toString().padStart(2, '0')}`;
  };

  const getTimerColor = () => {
    if (timer <= 10) return 'text-red-500 animate-pulse';
    if (timer <= 30) return 'text-orange-500';
    return 'text-green-500';
  };

  const getPhaseDisplay = () => {
    switch (gameState?.phase) {
      case 'waiting':
        return 'Waiting for game to start...';
      case 'ready':
        return 'Get ready! Game starting soon...';
      case 'battling':
        return 'WRITE YOUR PROMPT NOW!';
      case 'generating':
        return 'Generating images... Please wait!';
      case 'judging':
        return 'Images ready! Awaiting judgment...';
      case 'finished':
        return gameState.winner === playerId ? 'YOU WON! 🎉' : 'Better luck next time!';
      default:
        return 'Connecting...';
    }
  };

  if (error) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-red-900 to-red-800 text-white flex items-center justify-center">
        <div className="text-center">
          <AlertCircle className="h-16 w-16 mx-auto mb-4" />
          <h1 className="text-2xl font-bold mb-2">Connection Error</h1>
          <p className="mb-4">{error}</p>
          <button 
            onClick={() => window.location.reload()} 
            className="bg-white text-red-900 px-6 py-2 rounded-lg font-semibold hover:bg-gray-100"
          >
            Retry
          </button>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gradient-to-br from-purple-900 to-blue-900 text-white">
      {/* Header */}
      <div className="p-4 border-b border-purple-600 flex justify-between items-center">
        <div className="flex items-center space-x-4">
          <h1 className="text-3xl font-bold">Player {playerId}</h1>
          <div className="flex items-center space-x-2">
            {connected ? (
              <><Wifi className="h-5 w-5 text-green-500" /><span className="text-green-500">Connected</span></>
            ) : (
              <><WifiOff className="h-5 w-5 text-red-500" /><span className="text-red-500">Disconnected</span></>
            )}
          </div>
        </div>

        {gameState?.phase === 'battling' && (
          <div className={`text-6xl font-mono font-bold ${getTimerColor()}`}>
            {formatTime(timer)}
          </div>
        )}
      </div>

      {/* Main Content */}
      <div className="p-8 max-w-4xl mx-auto">
        {/* Game Status */}
        <div className="mb-8 text-center">
          <div className="text-2xl font-bold mb-4">
            {getPhaseDisplay()}
          </div>

          {gameState?.target && (
            <div className="bg-white/10 backdrop-blur rounded-lg p-6 mb-6">
              <h3 className="text-xl font-semibold mb-2">Target:</h3>
              <p className="text-lg">{gameState.target}</p>
            </div>
          )}
        </div>

        {/* Prompt Input */}
        {(gameState?.phase === 'battling' || gameState?.phase === 'ready') && (
          <div className="mb-8">
            <label className="block text-xl font-semibold mb-4">
              Your Prompt:
            </label>
            <textarea
              value={prompt}
              onChange={handlePromptChange}
              placeholder="Write your image generation prompt here..."
              disabled={gameState?.phase !== 'battling'}
              className="w-full h-48 p-6 text-lg bg-white/10 backdrop-blur border-2 border-purple-400 rounded-lg text-white placeholder-gray-300 focus:border-purple-300 focus:outline-none resize-none"
              maxLength={500}
            />
            <div className="text-right text-sm text-gray-300 mt-2">
              {prompt.length}/500 characters
            </div>
          </div>
        )}

        {/* Ready Button */}
        {gameState?.phase === 'ready' && !gameState?.players?.[playerId]?.ready && (
          <div className="text-center">
            <button
              onClick={handleReady}
              className="bg-green-600 hover:bg-green-700 text-white font-bold py-4 px-8 rounded-lg text-xl transition-colors flex items-center space-x-2 mx-auto"
            >
              <Zap className="h-6 w-6" />
              <span>I'm Ready!</span>
            </button>
          </div>
        )}

        {/* Waiting States */}
        {gameState?.phase === 'generating' && (
          <div className="text-center">
            <div className="inline-block animate-spin rounded-full h-16 w-16 border-b-2 border-white mb-4"></div>
            <p className="text-xl">Generating your image...</p>
          </div>
        )}

        {gameState?.phase === 'judging' && (
          <div className="text-center">
            <div className="text-6xl mb-4">⚖️</div>
            <p className="text-xl">The judges are deciding...</p>
          </div>
        )}

        {gameState?.phase === 'finished' && (
          <div className="text-center">
            {gameState.winner === playerId ? (
              <div className="text-6xl mb-4">🏆</div>
            ) : (
              <div className="text-6xl mb-4">🤝</div>
            )}
          </div>
        )}
      </div>

      {/* Footer Info */}
      <div className="fixed bottom-4 left-4 text-sm text-gray-300">
        Server: {getSocketURL()}
      </div>
    </div>
  );
}
